#[cfg(feature = "test-remote")]
mod remote {
    use serial_test::serial;

    macro_rules! impl_test_remote_block_receipt_root {
        ($(
            $name:ident => $block_number:literal,
        )+) => {
            $(
                paste::item! {
                    #[tokio::test]
                    #[serial]
                    async fn [<remote_block_receipt_root_ $name>]() -> anyhow::Result<()> {
                        use edr_chain_l1::{receipt::L1BlockReceipt, L1ChainSpec, TypedEnvelope};
                        use edr_eth::PreEip1898BlockSpec;
                        use edr_receipt::{log::FilterLog};
                        use edr_rpc_eth::client::EthRpcClientForChainSpec;
                        use edr_test_utils::env::JsonRpcUrlProvider;
                        use edr_trie::ordered_trie_root;

                        let client = EthRpcClientForChainSpec::<L1ChainSpec>::new(&JsonRpcUrlProvider::ethereum_mainnet(), edr_defaults::CACHE_DIR.into(), None)?;

                        let block = client
                            .get_block_by_number_with_transaction_data(PreEip1898BlockSpec::Number($block_number))
                            .await
                            .expect("Failed to retrieve block");

                        let receipts = client.get_transaction_receipts(block.transactions.iter().map(|transaction| &transaction.hash))
                            .await?
                            .expect("All receipts of a block should exist");

                        let receipts = receipts
                            .into_iter()
                            .map(L1BlockReceipt::<TypedEnvelope::<edr_receipt::Execution<FilterLog>>>::try_from)
                            .collect::<Result<Vec<_>, _>>()?;

                        let receipts_root = ordered_trie_root(
                            receipts
                                .into_iter()
                                .map(|receipt| alloy_rlp::encode(&receipt)),
                        );

                        assert_eq!(block.receipts_root, receipts_root);

                        Ok(())
                    }
                }
            )+
        };
    }

    impl_test_remote_block_receipt_root! {
        post_eip658 => 5_370_000u64,
        eip2930 => 12_751_000u64, // block contains at least one transaction with type 1
        eip1559 => 14_000_000u64, // block contains at least one transaction with type 2
    }

    macro_rules! impl_test_receipt_rlp_encoding {
        ($(
            $name:ident: $transaction_hash:literal => $encoding:literal,
        )+) => {
            $(
                paste::item! {
                    #[tokio::test]
                    async fn [<receipt_rlp_encoding_ $name>]() -> anyhow::Result<()> {
                        use alloy_rlp::Decodable as _;
                        use edr_chain_l1::{receipt::L1BlockReceipt, L1ChainSpec, TypedEnvelope};
                        use edr_primitives::B256;
                        use edr_receipt::{log::{ExecutionLog, FilterLog}, MapReceiptLogs as _};
                        use edr_rpc_eth::client::EthRpcClientForChainSpec;
                        use edr_test_utils::env::JsonRpcUrlProvider;
                        use tempfile::TempDir;

                        let tempdir = TempDir::new().unwrap();
                        let client = EthRpcClientForChainSpec::<L1ChainSpec>::new(&JsonRpcUrlProvider::ethereum_mainnet(), tempdir.path().into(), None).unwrap();

                        let transaction_hash = B256::from_slice(&hex::decode($transaction_hash).unwrap());

                        let receipt = client
                            .get_transaction_receipt(transaction_hash)
                            .await
                            .expect("Should succeed")
                            .expect("Receipt must exist");

                        let receipt = L1BlockReceipt::<TypedEnvelope::<edr_receipt::Execution<FilterLog>>>::try_from(receipt)
                            .expect("Failed to deserialize receipt");

                        // Generated by Hardhat
                        let expected = hex::decode($encoding).unwrap();

                        assert_eq!(alloy_rlp::encode(&receipt).to_vec(), expected, "RLP encoding should match");

                        let decoded = TypedEnvelope::<edr_receipt::Execution::<ExecutionLog>>::decode(&mut expected.as_slice()).unwrap();
                        let receipt = receipt.inner.inner.map_logs(ExecutionLog::from);

                        assert_eq!(decoded, receipt);

                        Ok(())
                    }
                }
            )+
        };
    }

    impl_test_receipt_rlp_encoding! {
        post_eip658: "1421a887a02301ae127bf2cd4c006116053c9dc4a255e69ea403a2d77c346cf5"
            => "",
        eip2930: "01361649690dbff1ac1da1a7351a125b7cb0f26b9c5e017c4aefe90135b9be14"
            => "",
        eip1559: "3dac2080b4c423029fcc9c916bc430cde441badfe736fc6d1fe9325348af80fd"
            => "",
    }
}
